function setCookie(e, o, t) {
	var i = new Date;
	i.setTime(i.getTime() + 24 * t * 60 * 60 * 1e3), document.cookie = e + "=" + o + ";expires=" + i
}

function getCookie(e) {
	if(document.cookie) {
		for(var o = document.cookie.replace(/;\s/g, ";").split(";"), t = 0; t < o.length; t++)
			if(item = o[t].split("="), item[0] == e) return brr = item[1], JSON.parse(brr);
		return []
	}
	return []
}

function removeCookie(e) {
	setCookie(e, "", -1)
}